-- Migration: FRM33/34/35 Supervisor Question Bank
-- Purpose: Create frm_supervisor_questions table and seed questions parsed
--          from the static HTML forms (frm33.html, frm34.html, frm35.html)
-- Date: 2025-11-18
-- =====================================================================

CREATE TABLE IF NOT EXISTS frm_supervisor_questions (
    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    form_id TEXT NOT NULL CHECK (form_id IN ('frm33', 'frm34', 'frm35')),
    question_code TEXT NOT NULL,
    question_text_en TEXT NOT NULL,
    question_text_tr TEXT,
    question_type TEXT NOT NULL DEFAULT 'text',
    is_required BOOLEAN NOT NULL DEFAULT TRUE,
    position INT NOT NULL,
    metadata JSONB DEFAULT '{}'::jsonb,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

CREATE UNIQUE INDEX IF NOT EXISTS idx_supervisor_questions_form_code
    ON frm_supervisor_questions(form_id, question_code);


